import React, { PureComponent } from 'react';
import Card from '../styles/Card';

class Modal extends PureComponent {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
    return (
      <div
        visible={this.props.visible}
        className="absolute top-0 bottom-0 left-0 right-0 bg-gray-400 opacity-75"
      >
        <Card className="max-w-sm mx-auto">{this.props.children}</Card>
      </div>
    );
  }
}

export default Modal;
